public static String[] mg_shuffleString(String[] a) {
int n = a.length;
for (int i = 0; i < n; i++) {  /** between i and n-1 */
	int r = i + (int) (Math.random() * (n - i));
	String tmp = a[i];    /** swap */
	a[i] = a[r];
	a[r] = tmp;
}
return a;
}

